export default {
  app: new Vue({
    el: "#app",
    vuetify: new Vuetify(),
    data() {
      return {
        isDark: true,
        show: true,
        newTodo: "",
        todos: [],
        day: this.todoDay(),
        date: new Date().getDate(),
        ord: this.nth(new Date().getDate()),
        year: new Date().getFullYear(),
        isTodoExist: false
      };
    },
    computed: {
      todoExists() {
        return this.isTodoExist ? "todo is already in the list add another one" : "";
      }
    },
    methods: {
      addTodo() {
        this.isTodoExist = false;
        const value = this.newTodo && this.newTodo.trim();
        if (!value) {
          return;
        }
        const isTodoExists = this.todos.find(todo => todo.title === value);
        if (!isTodoExists) {
          this.todos.push({
            title: this.newTodo,
            done: false
          });
          this.newTodo = "";
        }
        if (isTodoExists) {
          this.isTodoExist = true;
        }
      },
      removeTodo(index) {
        this.todos.splice(index, 1);
      },
      todoDay() {
        const d = new Date();
        const days = ["Sunday", "Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday"];
        return days[d.getDay()];
      },
      nth(d) {
        if (d > 3 && d < 21) return "th";
        switch (d % 10) {
          case 1:
            return "st";
          case 2:
            return "nd";
          case 3:
            return "rd";
          default:
            return "th";
        }
      }
    },
    filters: {
      capitalize: function (value) {
        if (!value) return "";
        value = value.toString();
        return value.charAt(0).toUpperCase() + value.slice(1);
      }
    }
  })
};
